INSERT INTO `resort_data` (`id`, `description`, `pic`) VALUES
	(1, 'Хургада – это курортный город в Египте, расположенный на Красном море. Вдоль побережья город растягивается почти на 40 километров, и вся прибрежная территория усеяна разнообразными отелями. Курорт известен благодаря большому количеству мест для занятий дайвингом. Приобрести необходимое снаряжение и взять уроки по погружению можно здесь же – в районе Саккала. Также в Хургаде много ресторанов, баров и ночных клубов. Есть и старый город Эль-Дахар, знаменитый своими кофейнями и рынками.', 'hurgada.jpg'),
	(2, 'Шарм-эль-Шейх – это курортный город в Египте, расположенный между пустыней Синайского полуострова и Красным морем. Шарм-эль-Шейх славится своими тихими песчаными пляжами, чистой водой и коралловыми рифами. Среди туристов популярен залив Наама, вдоль которого тянется окаймленная пальмами набережная с множеством баров и ресторанов. Ещё одно известное место – парк Рас-Мохаммед, который особенно привлекает любителей дайвинга. Здесь можно поплавать рядом с затонувшим кораблем "Тистлегорм" и посмотреть на рифы Шарк и Иоланда.', 'sharm-el-sheikh.jpg'),
	(3, 'Каир – многолюдная столица Египта на реке Нил. Сердце города – площадь Тахрир, на которой расположен огромный Египетский музей с мумиями фараонов и позолоченными деревянными статуями Тутанхамона. В Гизе, пригороде Каира, находятся всемирно известные пирамиды и Большой сфинкс, датируемый XXVI в. до н. э. С Каирской телебашни высотой 187 м, возведенной в зеленом районе Замалек на острове Гезира, открывается великолепный вид на город.', 'cair.jpg'),
	(4, 'Луксор – город на восточном берегу Нила на юге Египта. Он расположен на месте древних Фив – столицы египетских фараонов в XVI–XI веках до нашей эры, когда они находились на пике могущества. В городе сохранились два огромных архитектурных памятника – величественный Луксорский храм и находящийся на полтора километра севернее Карнакский храм. На западном берегу реки расположены Долина Царей и Долина Цариц, которые получили всемирную известность благодаря гробницам фараонов.', 'luxor.jpg'),
	(5, 'Дахаб  — курортный город в Египте, расположенный на востоке Синайского полуострова, на берегу Акабского залива Красного моря, в 100 км к северу от Шарм-эш-Шейха.\r\n\r\nВ Дахабе имеется Старый город, называемый Масбат, бедуинская деревня Ассала, район новостроек Мубарак, Медина и Лагуна Дахаба, где расположены дорогие отели и станции обучения виндсёрфингу. Старый город состоит из набережной, вдоль которой вплотную друг к другу находятся недорогие кэмпы, маленькие отели, рестораны, лавочки и кафе, а также несколько узких улиц. В бухте города можно найти развалины древнего порта, принадлежавшего набатейцам.', 'dahab.jpg'),
	(6, 'Стамбул – крупнейший город Турции на берегах пролива Босфор, который разделяет его на европейскую и азиатскую части. В Старом городе сохранились здания различных исторических эпох. В районе Султанахмет расположены возведенный римлянами Ипподром, где в течение многих столетий проводили гонки на колесницах, и Египетский обелиск. Византийский собор Святой Софии славится своим огромным куполом VI века и роскошными христианскими мозаиками.', 'stambul.jpg'),
	(7, 'Анкара – многонациональная столица Турции, расположенная в регионе Центральная Анатолия. Город славится своим Оперным театром, в котором выступают оперная и балетная труппы, Президентским симфоническим оркестром и несколькими национальными театрами. Над городом возвышается грандиозный мавзолей Аныткабир с могилой Кемаля Ататюрка – первого президента современной Турции, который в 1923 году перенес столицу в Анкару.', 'ankara.jpg'),
	(8, 'Анталья – курортный город, который славится своей Старой гаванью, где швартуются яхты, и пляжами, окруженными огромными отелями. Он расположен на Турецкой Ривьере – участке средиземноморского побережья Турции, который привлекает туристов благодаря прозрачным голубым водам. Ворота Адриана, построенные в честь посетившего Анталью в 130 году римского императора, и возведенная во II веке башня Хыдырлык, откуда открывается превосходный вид на гавань, напоминают о том периоде, когда город был важным портом Римской империи.', 'antalia.jpg'),
	(9, 'Финике — город и район в Турции, в 110 км к западу от Анталии. Расположен на побережье Средиземного моря.\r\n\r\nСовременный город вырос на месте античного города Финикия, основанного финикийцами в V веке до н. э. Но античный город утерян под современными постройками. В 655 году близ города произошло крупное морское сражение между византийским и арабским флотами.\r\n\r\nГород знаменит своими ароматными апельсинами.', 'fenike.jpg'),
	(10, 'Мармарис – курортный город Турецкой Ривьеры (известной также как Бирюзовый берег) с оживленным галечным пляжем и бульваром вдоль побережья. Город славится активной ночной жизнью на улице Баров, где под открытым небом расположено множество клубов и концертных площадок. Мармарис устроился в долине между горами, поросшими сосновым лесом, и прозрачными водами – прекрасным местом для парусного спорта и дайвинга.', 'marmaris.jpg'),
	(11, 'Нячанг – прибрежный курортный город на юге Вьетнама, примечательный своими пляжами, местами для дайвинга и островами. Главный пляж расположен вдоль улицы Чан Фу. Здесь же находится набережная с гостиницами и ресторанами морепродуктов. На близлежащий остров Че ведет канатная дорога, а до природных заповедников на островах Мун и Там с коралловыми рифами можно добраться на лодке.', 'nychang.jpg'),
	(12, 'Дананг – прибрежный город в центральной части Вьетнама, бывший французский порт. Он славится своими песчаными пляжами и служит отправной точкой для посещения холмов Бана, расположенных к западу от города во внутренней части страны. С перевала Хайван открывается прекрасный вид на залив Дананг и Мраморные горы – пять известняковых скал, где расположено несколько пагод и пещер, в которых хранятся буддийские святыни.', 'danang.jpg'),
	(13, 'Ханой – столица Вьетнама. Город известен древней архитектурой и богатой культурой с южно-азиатскими, китайскими и французскими мотивами. Хаотичный Старый квартал в самом сердце города состоит из множества узеньких улочек. Каждая из них предназначена для торговли определенным видом товара. В Ханое много небольших храмов, например Бать-Ма, построенный в честь легендарного коня. На рынке Донг-Суан можно приобрести предметы домашнего обихода и попробовать уличную еду.', 'hanoi.jpg'),
	(14, 'Кантхо — административная единица город центрального подчинения во Вьетнаме, самый крупный город в дельте Меконга. Население административной единицы в составе 5 городских районов собственно города Кантхо, а также 4 сельских пригородных районов составляет 1 214,1 тыс. жителей, в т.ч. городское 805 200 на 2012 год.\r\n\r\nРасположен в 1888 км от Ханоя и в 169 км от Хошимина.\r\n\r\nПлавучие рынки Кантхо — главная достопримечательность города. Фото 2009 г.\r\nНа протяжении всей своей истории город тесно связан с рекой. Ныне Кантхо является крупнейшим городом в дельте Меконга и одним из главных туристических центров дельты. Основные достопримечательности города — плавающие рынки (торговля происходит на лодках) и птичьи сады.\r\n\r\nСамые крупные вузы — Университет Кантхо и Институт рисоводства дельты Меконга].', 'cantho.jpg'),
	(15, 'Фукуок – это остров во Вьетнаме, расположенный недалеко от побережья Камбоджи в Сиамском заливе. Он известен своими белоснежными пляжами, окруженными пальмами, и роскошными курортами. Большинство из них расположены на юго-западном побережье. Более половины острова занимает национальный парк, где есть горы, тропические джунгли и дорожки для пешего туризма, а также представлены дикая флора и фауна. Самый крупный город острова – Дуонг-Донг. Здесь круглосуточно работают рынки, где можно приобрести авторские изделия, рыбу и другие товары.', 'fucuok.jpg'),
	(16, 'Гонконг – специальный административный район Китая на юго-востоке страны, бывшая британская колония. Этот динамичный густонаселенный город является крупным морским портом и мировым финансовым центром, силуэт которого формируют многочисленные небоскребы. Центральный район Гонконга славится своими офисными зданиями, среди которых Башня Банка Китая, построенная по проекту архитектора Бэя Юймина. Любители шопинга могут заказать костюм у портного или посетить Ночной рынок на улице Темпл-стрит.', 'hongkong.jpg'),
	(17, 'Гуанчжоу – огромный портовый город к северо-западу от Гонконга, лежащий на Жемчужной реке. Город славится своими авангардными зданиями, среди которых Оперный театр Гуанчжоу, построенный по проекту архитектора Захи Хадид и напоминающий окатанный морем морской камень, Музей провинции Гуандун, похожий на коробку с прорезями, и телебашня в форме тонких песочных часов. В храме Предков семейства Чэнь, основанном в 1894 году, открыт Гуандунский музей народного творчества.', 'guanchzhou.jpg'),
	(18, 'Пекин — столица и один из городов центрального подчинения Китайской Народной Республики. Пекин с трёх сторон окружён провинцией Хэбэй и граничит с Тяньцзинем на юго-востоке.\r\n\r\nЭто крупнейший железно- и автодорожный узел и один из основных авиаузлов страны. Кроме того, Пекин является политическим, образовательным и культурным центром КНР, в то время как главными экономическими центрами считаются Шанхай и Гонконг. Вместе с тем, в последнее время всё больше берёт на себя роль локомотива предпринимательской деятельности и основного поля для создания инновационных предприятий.\r\n\r\nВходит в число четырёх древних столиц Китая. В 2008 году в городе прошли Летние Олимпийские игры. В 2022 году в городе пройдут Зимние Олимпийские игры.', 'pekin.jpg'),
	(19, 'Шанхай — крупнейший по численности населения город Китая и мира. Расположен в дельте реки Янцзы на востоке Китая. Один из четырёх городов центрального подчинения КНР, важный финансовый и культурный центр страны, а также крупнейший в мире морской порт.', 'shankhay.jpg'),
	(20, 'Макао – специальный административный район Китая на юге страны, расположенный на противоположном от Гонконга берегу дельты Жемчужной реки. На культуру Макао большое влияние оказала Португалия, которая управляла территорией до 1999 года. Город часто называют азиатским Лас-Вегасом благодаря его гигантским казино и торговым центрам на бульваре Котай-стрип, соединяющем острова Тайпа и Колоан. Со смотровой площадки Башни Макао высотой 338 метров открывается впечатляющий вид на окрестности.', 'makao.jpg'),
	(21, 'Афины – столица современной Греции и центр древнегреческой цивилизации, которая славилась своим могуществом в эпоху античности. Над городом возвышается Акрополь – возведенная в V в. до нашей эры крепость, которую венчает храм Парфенон с его знаменитой колоннадой. В экспозиции Музея Акрополя и Национального археологического музея представлены скульптуры, вазы, ювелирные изделия и другие предметы материальной культуры Древней Греции.', 'athens.jpg'),
	(22, 'Аттика — юго-восточная область Центральной Греции, соединительное звено между Балканским полуостровом и Архипелагом, площадью приблизительно в 3808 км², граничит на севере с Беотией, на западе через Коринфский перешеек — с Мегаридой и всем Пелопоннесом. С юга омывается заливом Сароникос, с востока — заливом Петалия и северо-востока — заливом Нотиос-Эввоикос Эгейского моря.', 'attika.jpg'),
	(23, 'Крит – крупнейший остров Греции с разнообразным рельефом: побережье покрывают многочисленные пляжи с мелким песком, например Элафониси, а во внутренних районах высятся горные хребты, в том числе Лефка-Ори ("Белые горы"). Рядом с самой высокой горой этого хребта, Идой, находится вход в Идейскую пещеру, в которой согласно греческой мифологии родился Зевс. В столице Крита, Ираклионе, стоит посетить знаменитый Археологический музей с коллекцией минойского искусства и Кносс – город, основанный в бронзовом веке.', 'crete.jpg'),
	(24, 'Родос – крупнейший из островов принадлежащего Греции архипелага Додеканес. Он известен своими морскими курортами, древними руинами и зданиями, которые возвели в период крестовых походов рыцари-госпитальеры. В Старом городе Родоса, главного поселения острова, стоит посетить средневековую улицу Рыцарей и напоминающий крепость дворец Великих магистров, который был захвачен османами, а затем итальянцами. Теперь в нем открыт исторический музей.', 'rodos.jpg'),
	(25, 'Парнас  — священная гора в Греции (на западе Фокиды), связанная, подобно Олимпу, Геликону, Китерону, с мифическими сказаниями и известная местонахождением на её южном склоне дельфийского оракула. На протяжении более чем двух тысячелетий упоминается в культуре как символическое местообитание поэтов и, расширительно, вообще деятелей искусства.', 'parnas.jpg');
